const initState = {
    name: "dong",
    age: 27,
    password: "hihihi"
};

// 이것이 액션이다.
const changeName = (data) => {
    return {
        type: "CHANGE_NAME",
        data
    };
};

changeName("myNewName");
// name: "myNewName",
// age: 27,
// password: "hihihi"
// 이런 액션이 됨

// reducer: (이전상태, 액션) => 다음상태
const rootReducer = (state, action) => {
    switch (action.type) {
        case "CHANGE_NAME":
            return {
                ...state,
                name: action.data
            };
    }
};

export default rootReducer;
