import shortid from "shortid";

// user 기본 state 구조
const initialState = {
    mainPosts: [
        {
            id: 1,
            User: { id: 1, nickname: "동현이" },
            content: "동현이의 첫 번째 게시글! #아잉 #조아요",
            Images: [{ src: "https://expanddesk-media.s3.amazonaws.com/hosting/740px-Google_Docs_logo.svg.png" }, { src: "https://expanddesk-media.s3.amazonaws.com/hosting/logo_google-drive.png" }],
            Comments: [
                {
                    id: shortid.generate(),
                    User: { id: shortid.generate(), nickname: "동현삼" },
                    content: "꺄악",
                },
                {
                    id: shortid.generate(),
                    User: { id: shortid.generate(), nickname: "동현사" },
                    content: "아이조아",
                },
            ],
        },
    ],
    // 이미지 업로드할 때의 경로
    imagePaths: [],

    // 게시글 추가가 완료되었는지 여부
    addPostLoading: false,
    addPostDone: false,
    addPostError: null,

    // 댓글 추가가 완료되었는지 여부
    addCommentLoading: false,
    addCommentDone: false,
    addCommentError: null,
};

const dummyPost = (data) => ({
    id: data.id,
    content: data.content,
    User: { id: 1, nickname: "동현이" },
    Images: [],
    Comments: [],
});

const dummyComment = (data) => ({
    id: shortid.generate(),
    content: data,
    User: {
        id: 1,
        nickname: "동현이",
    },
});

// 액션 오타 방지용
export const ADD_POST_REQUEST = "ADD_POST_REQUEST";
export const ADD_POST_SUCCESS = "ADD_POST_SUCCESS";
export const ADD_POST_FAILURE = "ADD_POST_FAILURE";
export const REMOVE_POST_REQUEST = "REMOVE_POST_REQUEST";
export const REMOVE_POST_SUCCESS = "REMOVE_POST_SUCCESS";
export const REMOVE_POST_FAILURE = "REMOVE_POST_FAILURE";
export const ADD_COMMENT_REQUEST = "ADD_COMMENT_REQUEST";
export const ADD_COMMENT_SUCCESS = "ADD_COMMENT_SUCCESS";
export const ADD_COMMENT_FAILURE = "ADD_COMMENT_FAILURE";

export const addPostAction = (data) => {
    return {
        type: ADD_POST_REQUEST,
        data,
    };
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        // 포스트 추가 액션
        case ADD_POST_REQUEST:
            console.log("reducer add post request");
            return {
                ...state,
                addPostLoading: true,
                addPostDone: false,
                addPostError: null,
            };
        case ADD_POST_SUCCESS:
            console.log("reducer add post success");
            return {
                ...state,
                mainPosts: [dummyPost(action.data), ...state.mainPosts],
                addPostLoading: false,
                addPostDone: true,
            };
        case ADD_POST_FAILURE:
            console.log("reducer add post failure");
            return {
                ...state,
                addPostLoading: false,
                addPostError: action.error,
            };

        // 포스트 삭제 액션
        case REMOVE_POST_REQUEST:
            console.log("reducer add post request");
            return {
                ...state,
                addPostLoading: true,
                addPostDone: false,
                addPostError: null,
            };
        case REMOVE_POST_SUCCESS:
            console.log("reducer add post success");
            return {
                ...state,
                mainPosts: [dummyPost(action.data), ...state.mainPosts],
                addPostLoading: false,
                addPostDone: true,
            };
        case REMOVE_POST_FAILURE:
            console.log("reducer add post failure");
            return {
                ...state,
                addPostLoading: false,
                addPostError: action.error,
            };
        // 코멘트 액션
        case ADD_COMMENT_REQUEST:
            console.log("reducer add comment request");
            return {
                ...state,
                addCommentLoading: true,
                addCommentDone: false,
                addCommentError: null,
            };
        case ADD_COMMENT_SUCCESS: {
            const postIndex = state.mainPosts.findIndex((v) => v.id === action.data.postId);
            const post = { ...state.mainPosts[postIndex] };
            post.Comments = [dummyComment(action.data.content), ...post.Comments];
            const mainPosts = [...state.mainPosts];
            mainPosts[postIndex] = post;
            return {
                ...state,
                mainPosts,
                addCommentLoading: false,
                addCommentDone: true,
            };
        }
        case ADD_COMMENT_FAILURE:
            console.log("reducer add comment failure");
            return {
                ...state,
                addCommentLoading: false,
                addCommentError: action.error,
            };

        default:
            return state;
    }
};

export default reducer;
