import { all, fork, call, put, takeLatest, delay, throttle } from "redux-saga/effects";
import axios from "axios";
import shortid from "shortid";

import { LOAD_POSTS_REQUEST, LOAD_POSTS_SUCCESS, LOAD_POSTS_FAILURE, ADD_POST_REQUEST, ADD_POST_SUCCESS, ADD_POST_FAILURE, REMOVE_POST_REQUEST, REMOVE_POST_SUCCESS, REMOVE_POST_FAILURE, ADD_COMMENT_REQUEST, ADD_COMMENT_SUCCESS, ADD_COMMENT_FAILURE, generateDummyPost } from "../reducers/post";
import { ADD_POST_TO_ME, REMOVE_POST_OF_ME } from "../reducers/user";

// 게시물 불러오기
function loadPostsAPI(data) {
    return axios.get("/post", data);
}
function* loadPosts(action) {
    try {
        const result = yield call(loadPostsAPI, action.data);
        yield put({
            type: LOAD_POSTS_SUCCESS,
            data: generateDummyPost(10), // 불러올 게시물
        });
    } catch (err) {
        yield put({ type: LOAD_POSTS_FAILURE, error: err.response.data });
    }
}

// 게시글 추가
function addPostAPI(data) {
    return axios.post("/post", { content: data }); // req.body.content
}
function* addPost(action) {
    try {
        const result = yield call(addPostAPI, action.data);
        const id = shortid.generate();
        yield put({
            type: ADD_POST_SUCCESS,
            data: result.data,
        });
        yield put({
            type: ADD_POST_TO_ME,
            data: result.data.id,
        });
        console.log("saga add post success");
    } catch (err) {
        yield put({ type: ADD_POST_FAILURE, error: err.response.data });
    }
}

// 게시글 삭제
function removePostAPI(data) {
    return axios.delete("/post", data);
}
function* removePost(action) {
    try {
        // const result = yield call(addPostAPI, action.data);
        yield delay(2000);
        yield put({
            type: REMOVE_POST_SUCCESS,
            data: action.data, // 지울 게시글의 아이디가 들어있음
        });
        yield put({
            type: REMOVE_POST_OF_ME,
            data: action.data,
        });
        console.log("saga remove post success");
    } catch (err) {
        yield put({ type: REMOVE_POST_FAILURE, error: err.response.data });
    }
}

function addCommentAPI(data) {
    return axios.post(`/post/${data.postId}/comment`, data);
}
function* addComment(action) {
    try {
        const result = yield call(addCommentAPI, action.data);
        yield put({
            type: ADD_COMMENT_SUCCESS,
            data: result.data,
        });
        console.log("saga add comment success");
    } catch (err) {
        yield put({
            type: ADD_COMMENT_FAILURE,
            error: err.response.data,
        });
    }
}

function* watchLoadPost() {
    // 5초에 한 번만 실행
    yield throttle(5000, LOAD_POSTS_REQUEST, loadPosts);
}

function* watchAddPost() {
    yield takeLatest(ADD_POST_REQUEST, addPost);
}

function* watchRemovePost() {
    yield takeLatest(REMOVE_POST_REQUEST, removePost);
}

function* watchAddComment() {
    yield takeLatest(ADD_COMMENT_REQUEST, addComment);
}

export default function* postSaga() {
    yield all([fork(watchLoadPost), fork(watchAddPost), fork(watchRemovePost), fork(watchAddComment)]);
}
