// 기본 구조
const initialState = {
    mainPosts: [
        {
            id: 1,
            User: { id: 1, nickname: "동현이" },
            content: "동현이의 첫 번째 게시글! #앙 #기모찌",
            Images: [{ src: "https://expanddesk-media.s3.amazonaws.com/hosting/740px-Google_Docs_logo.svg.png" }, { src: "https://expanddesk-media.s3.amazonaws.com/hosting/logo_google-drive.png" }],
            Comments: [
                {
                    User: { nickname: "동현삼" },
                    content: "꺄악"
                },
                {
                    User: { nickname: "동현사" },
                    content: "아이조아"
                }
            ]
        }
    ],
    // 이미지 업로드할 때의 경로
    imagePaths: [],
    // 게시글 추가가 완료되었는지 여부
    postAdded: false
};

export const addPostAction = () => {
    return {
        type: "ADD_POST"
    };
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        default:
            return state;
    }
};

export default reducer;
