import { HYDRATE } from "next-redux-wrapper";

const initState = {
    user: {
        isLoggedIn: false,
        user: null,
        signUpData: {},
        loginData: {}
    },
    post: {
        mainPosts: []
    }
};

// 이것이 액션이다.
export const loginAction = (data) => {
    return {
        type: "LOG_IN",
        data
    };
};

export const logoutAction = () => {
    return {
        type: "LOG_OUT"
    };
};

// 이것이 reducer. (이전상태, 액션) => 다음상태
const rootReducer = (state, action) => {
    switch (action.type) {
        case "LOG_IN":
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: true,
                    user: action.data
                }
            };
        case "LOG_OUT":
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: false,
                    user: null
                }
            };
    }
};

export default rootReducer;
